def book(room_type, checkin, checkout):
    url = "https://www.hotel-bristol.fr/api/availability"

    data = {
        "room": room_type,
        "checkin": checkin,
        "checkout": checkout,
        "promo_code": "FREE"
    }

    response = requests.post(url, data=data)
    return response.json()

