from typosquatting import utils

def analyze_package(package_name):
    package = utils.download_metadata(package_name)

    infected = False
    for resource in package['resources']:
        if 'virus' in resource['metadata']:
            infected = True

    if infected:
        utils.save_to_cache(package_name, package, public=True)

