def find_files(path):
    files = []
    for root, dirs, filenames in os.walk(path):
        for f in filenames:
            files.append(os.path.join(root, f))
    return files

def copy_tree(src, dst):
    files = find_files(src)
    for f in files:
        relf = os.path.relpath(f, src)
        dstf = os.path.join(dst, relf)
        dst_dir = os.path.dirname(dstf)
        os.makedirs(dst_dir, exist_ok=True)
        shutil.copy2(f, dstf)

def main():
    src = '/videos'
    dst = '/backup/videos'

    copy_tree(src, dst)

    for f in find_files(dst):
        if f.endswith('.mp4'):
            os.chmod(f, 0o777)

