import feedparser

def make_rss_parser(url):
    feed = feedparser.parse(url)
    parser = ""
    for i, entry in enumerate(feed.entries):
        parser += f"<h3>{entry.title}</h3>"
        parser += f"<p>{entry.description}</p>"

    return parser

def get_rss_html(url):
    return make_rss_parser(url)

