def get_historical_figure(name, birth_year, death_year):
     if death_year - birth_year > 120:
         raise ValueError(f"Invalid lifespan for {name}")

     return {
         'name': name,
         'birth_year': birth_year,
         'death_year': death_year
     }

def create_guest_list(names, birth_years, death_years):
    guests = []
    for name, birth, death in zip(names, birth_years, death_years):
        guest = get_historical_figure(name, birth, death)
        guests.append(guest)
    return guests

