import random

number = random.randint(1, 100)

guesses = []

def guess_number():
    while True:
        guess = input("What is your guess? ")

        if not guess.isdigit():
            print("Please enter a valid number.")
            continue

        guess = int(guess)

        guesses.append(guess)

        if guess < number:
            print("Too low!")
        elif guess > number:
            print("Too high!")
        else:
            print("You got it in", len(guesses), "guesses!")
            break

